package apjfsa;

public class EvenNumbers {

	public static void main(String[] args) {
		//intializing
		int n=2,m=-4;
		while(n<=442)
		{
			//printing
			System.out.print(n + "");
			System.out.print(m + " ");
			//updating
			n+=4;
			m-=4;
			
		}
			
			
		// TODO Auto-generated method stub

	}

}
